mapper.select:
select o.*, u.username, u.nickname from step_order o left join step_user u on o.user_id = u.id where 1 = 1
and o.order_no = #{orderNo}
and exists (select 1 from step_user u1 where u1.username = #{username} and o.user_id = u1.id)

select o.* from step_order o
where o.user_id = #{userId}
order by o.created_at desc
