mapper.select:
select p.* from step_product p where 1=1


    AND p.sku_name ILIKE #{pattern}


    AND p.status = #{status}

order by p.modified_at desc

select distinct r.* from (select pr.*, ad.sort_index from step_product pr left join step_advertisement ad on pr.id = ad.product_id left join step_product_classification_ref pc on pr.id = pc.product_id left join step_classification cl on pc.classification_id = cl.id where pr.status = 'ON_SHELF' and COALESCE(ad.ads_type, 'RECOMMEND') = 'RECOMMEND'
order by CASE WHEN cl.max_age <= #{maxAge} or cl.min_age >= #{minAge} THEN 1 ELSE 2 END
) r order by r.sort_index asc
