mapper.select:
select p.*, bs.code book_set_code, bs.name book_set_name
from step_product p left join step_book_set bs on p.book_set_id = bs.id
where p.id = #{id}

select p.* from step_product p where 1=1


    AND p.sku_name ILIKE #{pattern}


    AND p.status = #{status}

order by p.modified_at desc

select distinct m.* from (select p.* from step_product p left join step_advertisement a on p.id = a.product_id left join step_product_classification_ref pc on p.id = pc.product_id left join step_classification c on pc.classification_id = c.id where p.status = 'ON_SHELF' order by a.sort_index asc
, CASE WHEN c.max_age <= #{maxAge} or c.min_age >= #{minAge} THEN 1 ELSE 2 END
) m
